"use client"
import { motion } from "framer-motion"

export default function CoordinateInput({ label, name, value, onChange, disabled }) {
  return (
    <div className="flex flex-col space-y-1">
      <label className="text-sm font-bold text-cyan-300">{label}</label>
      <motion.input
        type="number"
        value={value}
        disabled={disabled}
        onChange={(e) => onChange(name, Number.parseFloat(e.target.value))}
        className="bg-blue-950/80 border border-blue-500/30 rounded-md px-3 py-2 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50 shadow-inner"
        whileFocus={{ boxShadow: "0 0 0 2px rgba(59, 130, 246, 0.5), 0 0 15px rgba(59, 130, 246, 0.3)" }}
      />
    </div>
  )
}
