import React, { useState, useEffect } from 'react';
import ControlPanel from './components/ControlPanel';
import ReplayMode from './components/ReplayMode';
import IMUMode from './components/IMUMode';
import InfoPanel from './components/InfoPanel';
import ModeSwitcher from './components/ModeSwitcher';
import LiveCamera from './components/LiveCamera';
import WaitingPage from './components/WaitingPage';

export default function App() {
  const [activeMode, setActiveMode] = useState('manual');
  const [robotReady, setRobotReady] = useState(false);
  const [waiting, setWaiting] = useState(true);

  useEffect(() => {
    document.documentElement.classList.add('dark'); // üëà Forces dark mode always
  }, []);  

  // ü§ñ Robot readiness check on initial page load
  useEffect(() => {
    console.log("[App] Sending GET /api/arm-ready...");
    fetch('/api/arm-ready')
      .then(res => res.json())
      .then(data => {
        if (data.ready) {
          console.log("[App] ‚úÖ Robot is ready!");
          setRobotReady(true);
        } else {
          console.warn("[App] ‚ùå Robot not ready:", data.error);
        }
      })
      .catch(err => {
        console.error("[App] Network error while waiting for robot:", err.message);
      })
      .finally(() => {
        setWaiting(false);
      });
  }, []);

  // ‚è≥ Show loading screen until robot is ready
  if (!robotReady || waiting) {
    return <WaitingPage />;
  }

  return (
    <div className="min-h-screen font-sans bg-gradient-to-br from-blue-900/70 to-black/90 text-white backdrop-blur-md dark:bg-gray-950">
      {/* Header */}
      <header className="flex justify-between items-center px-6 py-4 bg-blue-500/30 backdrop-blur-xl shadow-lg border-b border-blue-300/20">
        <h1 className="text-2xl font-bold tracking-wide text-blue-100 drop-shadow-sm">Lunar Arm Control</h1>
        <span className="text-sm text-blue-200 opacity-70">v1.0.0</span>
      </header>

      {/* Grid Layout */}
      <div className="grid grid-cols-5 grid-rows-[1fr_auto] gap-4 p-6">
        {/* Left Info Panel */}
        <div className="col-span-1">
          <InfoPanel />
        </div>

        {/* Center Control Mode Panel */}
        <div className="col-span-3">
          {activeMode === 'manual' && <ControlPanel />}
          {activeMode === 'replay' && <ReplayMode />}
          {activeMode === 'imu' && <IMUMode />}
        </div>

        {/* Right Mode Switcher Panel */}
        <div className="col-span-1">
          <ModeSwitcher activeMode={activeMode} setActiveMode={setActiveMode} />
        </div>

        {/* Bottom Camera Feed */}
        <div className="col-span-5 mt-6">
          <LiveCamera />
        </div>
      </div>
    </div>
  );
}
