export default function ModeSwitcher({ activeMode, setActiveMode }) {
    const modes = ['manual', 'replay', 'imu'];

    return (
      <div className="flex flex-col gap-4 p-4">
        {modes.map(mode => (
          <button
            key={mode}
            onClick={() => setActiveMode(mode)}
            className={`px-4 py-2 rounded text-sm transition border
              ${activeMode === mode
                ? 'bg-blue-600 text-white shadow-lg'
                : 'bg-white/10 hover:bg-white/20 text-blue-200 border-blue-300/20'}`}
          >
            {mode.toUpperCase()}
          </button>
        ))}
      </div>
    );
  }
