"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from "framer-motion"

const REPLAY_OPTIONS = [
  { id: 0, name: "Harvest the Plant" },
  { id: 1, name: "Draw a Circle" },
  { id: 2, name: "Wave Hello" },
]

export default function ReplayMode() {
  const [selectedId, setSelectedId] = useState(null)
  const [status, setStatus] = useState("Idle")
  const [messages, setMessages] = useState([])
  const [isRunning, setIsRunning] = useState(false)
  const [startTime, setStartTime] = useState(null)
  const scrollRef = useRef(null)
  const [latestCoord, setLatestCoord] = useState(null)
  const [coordHistory, setCoordHistory] = useState([])
  const gridRef = useRef(null)

  const startReplay = () => {
    if (selectedId === null) return

    setStatus("Sending replay request...")
    setMessages([])
    setCoordHistory([])
    setLatestCoord(null)
    setIsRunning(true)
    setStartTime(Date.now())

    fetch("/api/start-replay", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ index: selectedId }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) {
          setStatus("Replay started!")
        } else {
          setStatus("âŒ Failed to start replay.")
          setIsRunning(false)
        }
      })
      .catch((err) => {
        setStatus("âŒ Error contacting server.")
        setIsRunning(false)
      })
  }

  const clearLog = () => {
    setMessages([])
    setCoordHistory([])
  }

  useEffect(() => {
    if (!isRunning) return

    const interval = setInterval(() => {
      fetch("/api/replay-status")
        .then((res) => res.json())
        .then((data) => {
          if (data.messages) {
            setMessages((prev) => {
              const updated = [
                ...prev,
                ...data.messages.map((msg) => ({
                  ...msg,
                  timestamp: ((Date.now() - startTime) / 1000).toFixed(2) + "s",
                })),
              ]

              const lastUpdate = data.messages.reverse().find((m) => m.type === "update")
              if (lastUpdate && lastUpdate.data) {
                const correctedCoord = {
                  x: lastUpdate.data.y,
                  y: lastUpdate.data.x,
                  z: lastUpdate.data.z,
                  time: Date.now(),
                }
                setLatestCoord(correctedCoord)
                setCoordHistory((prev) => [...prev, correctedCoord])
              }

              return updated
            })
          }
          if (data.done) {
            setStatus("âœ… Replay complete.")
            setIsRunning(false)
            clearInterval(interval)
          }
        })
        .catch((err) => {
          setStatus("âŒ Error polling status")
          setIsRunning(false)
          clearInterval(interval)
        })
    }, 100)

    return () => clearInterval(interval)
  }, [isRunning, startTime])

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight
    }
  }, [messages])

  useEffect(() => {
    if (coordHistory.length > 10) {
      const now = Date.now()
      setCoordHistory((prev) => prev.filter((coord) => now - coord.time < 3000))
    }
  }, [coordHistory])

  const coordToPixel = (x, y) => {
    if (!gridRef.current) return { x: 0, y: 0 }
    const gridWidth = gridRef.current.clientWidth
    const gridHeight = gridRef.current.clientHeight
    const normalizedX = (x + 30) / 60
    const pixelX = normalizedX * gridWidth
    const normalizedY = 1 - y / 40
    const pixelY = normalizedY * gridHeight
    return { x: pixelX, y: pixelY }
  }

  return (
    <div className="bg-gradient-to-t from-black to-blue-950/90 border border-blue-500/30 shadow-2xl rounded-lg p-6 space-y-6 font-display">
      <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-400 tracking-wide text-center">
        Replay Mode
      </h2>

      <div className="space-y-2">
        {REPLAY_OPTIONS.map((option) => (
          <button
            key={option.id}
            onClick={() => setSelectedId(option.id)}
            className={`block w-full text-left px-4 py-3 rounded-lg transition shadow-md border text-sm font-bold
              ${
                selectedId === option.id
                  ? "bg-gradient-to-r from-blue-800 to-blue-900 text-white border-blue-400 shadow-[0_0_15px_rgba(59,130,246,0.5)]"
                  : "bg-blue-950/80 hover:bg-blue-900/60 border-blue-500/20 hover:border-blue-500/40"
              }`}
            disabled={isRunning}
          >
            {option.name}
          </button>
        ))}
      </div>

      <motion.button
        onClick={startReplay}
        disabled={selectedId === null || isRunning}
        className="w-full mt-4 bg-gradient-to-r from-green-600 to-emerald-700 hover:from-green-500 hover:to-emerald-600 text-white font-bold py-3 px-4 rounded-lg disabled:opacity-50 shadow-lg shadow-green-900/50 hover:shadow-green-500/30 hover:shadow-[0_0_20px_rgba(16,185,129,0.6)] disabled:shadow-none"
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
      >
        {isRunning ? "Running..." : "Start Replay"}
      </motion.button>

      <motion.button
        onClick={clearLog}
        className="w-full bg-gradient-to-r from-red-700 to-red-800 hover:from-red-600 hover:to-red-700 text-white py-2 px-2 mt-2 rounded-lg shadow-md shadow-red-900/50 hover:shadow-red-500/30 hover:shadow-[0_0_15px_rgba(239,68,68,0.5)] font-bold"
        disabled={messages.length === 0}
        whileHover={{ scale: 1.02 }}
        whileTap={{ scale: 0.98 }}
      >
        Clear Log
      </motion.button>

      <div className="mt-6 p-4 bg-black/80 rounded-lg text-sm max-h-64 overflow-y-auto border border-blue-500/30 shadow-inner" ref={scrollRef}>
        <p className="font-bold text-cyan-300 mb-2">Replay Status:</p>
        <ul className="space-y-1 text-blue-100">
          {messages.map((msg, i) => {
            if (msg.type === "update" && msg.data) {
              const { x: y, y: x, z } = msg.data;
              return (
                <li key={i} className="flex items-center space-x-2">
                  <span className="text-gray-400">ðŸ•’</span>
                  <span className="text-cyan-200">{msg.timestamp}</span>
                  <span className="text-gray-400">âž¤</span>
                  <span className="text-green-300 font-mono">
                    x: {x}, y: {y}, z: {z}
                  </span>
                </li>
              )
            }
            return null
          })}
        </ul>
        <p className="mt-2 italic text-xs text-cyan-300">Status: {status}</p>
      </div>

      <div className="mt-4 bg-black/80 p-4 rounded-lg shadow-xl relative border border-blue-500/30">
        <p className="font-bold text-sm text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-blue-400 mb-2">
          Live Position Grid (x vs y in cm)
        </p>
        <div ref={gridRef} className="relative w-full h-80 border border-blue-400/30 bg-gradient-to-b from-blue-950/90 to-black overflow-hidden rounded-md shadow-inner">
          {/* Green trail lines between coordinates */}
          {coordHistory.length > 1 && coordHistory.map((point, i) => {
            if (i === 0) return null
            const from = coordToPixel(coordHistory[i - 1].x, coordHistory[i - 1].y)
            const to = coordToPixel(point.x, point.y)
            const dx = to.x - from.x
            const dy = to.y - from.y
            const length = Math.sqrt(dx * dx + dy * dy)
            const angle = Math.atan2(dy, dx) * (180 / Math.PI)
            return (
              <div
                key={`line-${i}`}
                className="absolute bg-green-400/20"
                style={{
                  left: from.x + "px",
                  top: from.y + "px",
                  width: `${length}px`,
                  height: "1px",
                  transform: `rotate(${angle}deg) translateY(-50%)`,
                  transformOrigin: "0 0",
                }}
              />
            )
          })}

          {/* History trail dots */}
          <AnimatePresence>
            {coordHistory.map((coord) => {
              const { x, y } = coordToPixel(coord.x, coord.y)
              return (
                <motion.div
                  key={`history-${coord.time}`}
                  className="absolute w-2 h-2 rounded-full bg-green-500/30"
                  style={{
                    left: `${x}px`,
                    top: `${y}px`,
                    transform: "translate(-50%, -50%)",
                  }}
                  initial={{ opacity: 0.8, scale: 0.5 }}
                  animate={{ opacity: 0.3, scale: 0.7 }}
                  exit={{ opacity: 0 }}
                  transition={{ duration: 1 }}
                />
              )
            })}
          </AnimatePresence>

          {/* Current dot */}
          <AnimatePresence>
            {latestCoord && (
              <motion.div
                key="current-position"
                className="absolute w-4 h-4 rounded-full bg-green-400 shadow-lg shadow-green-500/50"
                style={{
                  left: `${coordToPixel(latestCoord.x, latestCoord.y).x}px`,
                  top: `${coordToPixel(latestCoord.x, latestCoord.y).y}px`,
                  transform: "translate(-50%, -50%)",
                  filter: "drop-shadow(0 0 12px rgba(74, 222, 128, 0.9))",
                }}
                initial={{ scale: 0.5, opacity: 0.5 }}
                animate={{ scale: [1, 1.2, 1], opacity: 1 }}
                transition={{ duration: 0.5, ease: "easeOut" }}
              />
            )}
          </AnimatePresence>

          {/* Axis labels */}
          <div className="absolute bottom-1 right-2 text-xs text-blue-300/70 font-bold">x-axis (cm)</div>
          <div className="absolute top-2 left-2 text-xs text-blue-300/70 font-bold">y-axis (cm)</div>
        </div>
      </div>
    </div>
  )
}
