// components/ReplayMode.jsx
// components/ReplayMode.jsx
import React, { useState, useEffect, useRef } from 'react';

const REPLAY_OPTIONS = [
  { id: 0, name: 'Harvest the Plant' },
  { id: 1, name: 'Draw a Circle' },
  { id: 2, name: 'Wave Hello' },
];

export default function ReplayMode() {
  const [selectedId, setSelectedId] = useState(null);
  const [status, setStatus] = useState('Idle');
  const [messages, setMessages] = useState([]);
  const [isRunning, setIsRunning] = useState(false);
  const [startTime, setStartTime] = useState(null);
  const scrollRef = useRef(null);
  const [latestCoord, setLatestCoord] = useState(null);

  const startReplay = () => {
    if (selectedId === null) return;

    setStatus('Sending replay request...');
    setMessages([]);
    setIsRunning(true);
    setStartTime(Date.now());

    fetch('/api/start-replay', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ index: selectedId })
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setStatus('Replay started!');
        } else {
          setStatus('❌ Failed to start replay.');
          setIsRunning(false);
        }
      })
      .catch(err => {
        setStatus('❌ Error contacting server.');
        setIsRunning(false);
      });
  };

  const clearLog = () => {
    setMessages([]);
  };

  useEffect(() => {
    if (!isRunning) return;

    const interval = setInterval(() => {
      fetch('/api/replay-status')
        .then(res => res.json())
        .then(data => {
          if (data.messages) {
            setMessages(prev => {
              const updated = [...prev, ...data.messages.map(msg => ({
                ...msg,
                timestamp: ((Date.now() - startTime) / 1000).toFixed(1) + 's'
              }))];
              const lastUpdate = data.messages.reverse().find(m => m.type === 'update');
              if (lastUpdate && lastUpdate.data) {
                setLatestCoord({ x: lastUpdate.data.x, y: lastUpdate.data.y });
              }
              return updated;
            });
          }
          if (data.done) {
            setStatus('✅ Replay complete.');
            setIsRunning(false);
            clearInterval(interval);
          }
        })
        .catch(err => {
          setStatus('❌ Error polling status');
          setIsRunning(false);
          clearInterval(interval);
        });
    }, 1000);

    return () => clearInterval(interval);
  }, [isRunning, startTime]);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="max-w-xl mx-auto bg-blue-900/40 border border-blue-300/20 shadow-lg rounded-xl p-6 space-y-6">
      <h2 className="text-xl font-bold mb-2">Replay Mode</h2>

      <div className="space-y-2">
        {REPLAY_OPTIONS.map(option => (
          <button
            key={option.id}
            onClick={() => setSelectedId(option.id)}
            className={`block w-full text-left px-4 py-2 rounded-lg transition shadow-sm border text-sm
              ${selectedId === option.id ? 'bg-blue-600 text-white' : 'bg-blue-200/10 hover:bg-blue-300/10 border-blue-400/10'}`}
            disabled={isRunning}
          >
            {option.name}
          </button>
        ))}
      </div>

      <button
        onClick={startReplay}
        disabled={selectedId === null || isRunning}
        className="w-full mt-4 bg-green-600 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded disabled:opacity-50"
      >
        {isRunning ? 'Running...' : 'Start Replay'}
      </button>

      <button
        onClick={clearLog}
        className="w-full bg-red-500 hover:bg-red-600 text-white text-xs py-1 px-2 mt-2 rounded"
        disabled={messages.length === 0}
      >
        Clear Log
      </button>

      <div className="mt-6 p-4 bg-black/40 rounded text-sm max-h-64 overflow-y-auto border border-blue-400/20" ref={scrollRef}>
        <p className="font-bold text-blue-100 mb-2">Replay Status:</p>
        <ul className="space-y-1 text-blue-200">
          {messages.map((msg, i) => {
            if (msg.type === 'update' && msg.data) {
              const { x, y, z } = msg.data;
              return (
                <li key={i}>
                  🕒 {msg.timestamp} ➤ <span className="text-green-300">x: {x}, y: {y}, z: {z}</span>
                </li>
              );
            }
            if (msg.type === 'bounds') {
              return (
                <li key={i} className="text-yellow-300">
                  🕒 {msg.timestamp} ⚠️ Out of bounds!
                </li>
              );
            }
            if (msg.type === 'done') {
              return (
                <li key={i} className="text-green-400 font-semibold">
                  🕒 {msg.timestamp} ✅ Replay Complete
                </li>
              );
            }
            if (msg.type === 'error') {
              return (
                <li key={i} className="text-red-400">
                  🕒 {msg.timestamp} ❌ Error: {msg.message}
                </li>
              );
            }
            return (
              <li key={i} className="text-gray-400 italic">
                🕒 {msg.timestamp} {JSON.stringify(msg)}
              </li>
            );
          })}
        </ul>
        <p className="mt-2 italic text-xs text-blue-300">Status: {status}</p>
      </div>

      <div className="mt-4 bg-blue-950 p-4 rounded shadow-md relative">
        <p className="font-semibold text-sm text-white mb-2">Live Position Grid (x vs y in cm)</p>
        <div className="relative w-full h-64 border border-blue-300 bg-blue-900/40 overflow-hidden">
          <div className="absolute inset-0">
            {Array.from({ length: 50 }, (_, i) => (
              <div
                key={`h-${i}`}
                className="absolute w-full border-t border-white/10"
                style={{ top: `${(i * 2)}px` }}
              />
            ))}
            {Array.from({ length: 50 }, (_, i) => (
              <div
                key={`v-${i}`}
                className="absolute h-full border-l border-white/10"
                style={{ left: `${(i * 2)}px` }}
              />
            ))}
          </div>
          {latestCoord && (
            <div
              className="absolute w-3 h-3 rounded-full bg-green-500 shadow"
              style={{
                left: `calc(50% + ${latestCoord.x * 10}px)`,
                top: `calc(50% - ${latestCoord.y * 10}px)`,
                transform: 'translate(-50%, -50%)'
              }}
            ></div>
          )}
        </div>
      </div>
    </div>
  );
}
